@Schema({ _id: false })
export class Schedule {
  @Prop({ required: true })
  departureTime: string; // "06:00"

  @Prop({ required: true })
  arrivalTime: string; // "12:00"

  @Prop({ enum: FrequencyType, default: FrequencyType.DAILY })
  frequency: FrequencyType;

  @Prop({ type: [Number], default: [1, 2, 3, 4, 5, 6, 0] }) // 0=Sunday, 1=Monday, etc.
  operatingDays: number[];

  @Prop({ required: true })
  effectiveFrom: Date;

  @Prop({ required: true })
  effectiveTo: Date;
}

@Schema({ _id: false })
export class Pricing {
  @Prop({ required: true })
  baseFare: number; // Base fare in LKR

  @Prop({ default: 2.5 })
  farePerKm: number; // Per km rate

  @Prop({ default: false })
  dynamicPricing: boolean;

  @Prop({ default: 1.0 })
  peakHourMultiplier: number;

  @Prop({ default: 1.0 })
  weekendMultiplier: number;

  @Prop({ default: 1.0 })
  holidayMultiplier: number;
}

