import { Prop, Schema, SchemaFactory } from '@nestjs/mongoose';
import { Document, Types } from 'mongoose';

export type RouteDocument = Route & Document & { _id: Types.ObjectId };

@Schema({ _id: false })
export class Location {
  @Prop({ required: true })
  city: string;

  @Prop({ required: true })
  station: string;

  @Prop({ type: [Number], required: true }) // [longitude, latitude]
  coordinates: number[];
}

@Schema({ _id: false })
export class IntermediateStop {
  @Prop({ required: true })
  city: string;

  @Prop({ required: true })
  station: string;

  @Prop({ type: [Number], required: true })
  coordinates: number[];

  @Prop({ required: true })
  arrivalTime: string; // "14:30"

  @Prop({ required: true })
  departureTime: string; // "14:45"

  @Prop({ required: true })
  distanceFromOrigin: number; // in km

  @Prop({ required: true })
  fareFromOrigin: number; // in LKR

  @Prop({ default: 10 })
  stopDuration: number; // in minutes
}

@Schema({ timestamps: true })
export class Route {
  @Prop({ required: true })
  routeName: string;

  @Prop({ type: Location, required: true })
  origin: Location;

  @Prop({ type: Location, required: true })
  destination: Location;

  @Prop({ type: [IntermediateStop], default: [] })
  intermediateStops: IntermediateStop[];

  @Prop({ required: true })
  totalDistance: number; // in km

  @Prop({ required: true })
  estimatedDuration: number; // in minutes

  @Prop({ default: true })
  isActive: boolean;

  @Prop({ type: Types.ObjectId, ref: 'User', required: true })
  createdBy: Types.ObjectId;

  @Prop({ type: [String], default: [] })
  popularTimes: string[]; // ["06:00", "18:00"]

  @Prop({ type: [String], default: [] })
  operatingDays: string[]; // ["monday", "tuesday", ...]
}

export const RouteSchema = SchemaFactory.createForClass(Route);

// Indexes for better performance
RouteSchema.index({ 'origin.city': 1, 'destination.city': 1 });
RouteSchema.index({ isActive: 1 });
RouteSchema.index({ createdBy: 1 });
RouteSchema.index({ routeName: 1 });

import { Prop, Schema, SchemaFactory } from '@nestjs/mongoose';
import { Document, Types } from 'mongoose';

export type UserDocument = User &
  Document & {
    _id: Types.ObjectId;
  };

export enum UserRole {
  ADMIN = 'admin',
  BUS_OWNER = 'bus_owner',
  DRIVER = 'driver',
  CONDUCTOR = 'conductor',
  PASSENGER = 'passenger',
}

export enum UserStatus {
  ACTIVE = 'active',
  SUSPENDED = 'suspended',
  PENDING = 'pending',
}

@Schema({ timestamps: true })
export class User {
  @Prop({ required: true, unique: true })
  email: string;

  @Prop({ required: true, select: false })
  password: string;

  @Prop({ required: true, enum: UserRole })
  role: UserRole;

  @Prop({
    type: {
      firstName: { type: String, required: true },
      lastName: { type: String, required: true },
      phone: { type: String, required: true },
      dateOfBirth: { type: Date },
      gender: { type: String, enum: ['male', 'female', 'other'] },
      address: {
        street: String,
        city: String,
        province: String,
        postalCode: String,
        country: { type: String, default: 'Sri Lanka' },
      },
      profilePicture: String,
    },
  })
  profile: {
    firstName: string;
    lastName: string;
    phone: string;
    dateOfBirth?: Date;
    gender?: string;
    address?: {
      street?: string;
      city?: string;
      province?: string;
      postalCode?: string;
      country?: string;
    };
    profilePicture?: string;
  };

  @Prop({
    type: {
      emailVerified: { type: Boolean, default: false },
      phoneVerified: { type: Boolean, default: false },
      documentsVerified: { type: Boolean, default: false },
    },
    default: {},
  })
  verification: {
    emailVerified: boolean;
    phoneVerified: boolean;
    documentsVerified: boolean;
  };

  @Prop({ enum: UserStatus, default: UserStatus.PENDING })
  status: UserStatus;

  @Prop({ select: false })
  refreshToken?: string;
}

export const UserSchema = SchemaFactory.createForClass(User);

// Index for better query performance
UserSchema.index({ email: 1 });
UserSchema.index({ role: 1 });
UserSchema.index({ status: 1 });

